import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';




class App extends Component {
  constructor(props) {
    super(props)


    this.state = {

      list: [],

      new_ToDo_Item: " "

    }
  }


  update_new_to_do_item(key, value) {

    this.setState({
      [key]: value
    })

  }


  add_new_item() {

    const new_ToDo_Item = {
      id: 1 + Math.random(),
      value: this.state.new_ToDo_Item.slice()
    }

    const list = [...this.state.list]

    list.push(new_ToDo_Item);

    this.setState({
      list,
      new_ToDo_Item: ""
    })

  }

  delete_item(id) {

    const list = [...this.state.list]

    const newlist = list.filter(item => item.id !== id)

    this.setState({
      list: newlist,
      new_ToDo_Item: ""
    })

  }





  render() {
    return (
      <div>
        <h1 className="app-title">ToDo List</h1>
        <div className="Container" >


          <div
            style={{
              padding: 30,
              textAlign: "left",
              maxWidth: 500,
              margin: "auto"
            }}
          ></div>
          add an item
          <br />
          <input
            placeholder="what do you want toDo ðŸ˜Š ?"

            type="text"

            id="item2"

            value={this.state.new_ToDo_Item}

            onChange={e => this.update_new_to_do_item("new_ToDo_Item", e.target.value)}
          >
          </input>
          <button
            className="add-btn btn-floating"
            onClick={() => this.add_new_item()}
            disabled={!this.state.new_ToDo_Item.length}
            >

            <i
            class="material-icons"
            >+</i>
            </button>
          <br /><br/>
          <ul>
            {this.state.list.map(item => {
              return (
                <li key={item.id} >
                  {this.value}
                  <button className="btn btn-floating" onClick={() => this.delete_item(item.id)} >
                  <i class="material-icons">x</i>
                </button>
                </li>
              )


            })}
          </ul>
        </div>
      </div>
    );
  }


}
ReactDOM.render(<App />, document.getElementById('root'));
{/* export default App; */}
