import React, { Component } from 'react';
//import logo from './logo.svg';
import './App.css';




class App extends Component {
  constructor(props) {
    super(props)


    this.state = {

      list: [],

      newTodoItem: " "

    }
  }


  updateTodoItem(key, value) {

    this.setState({
      [key]: value
    })

  }


  add_new_item() {

    const new_ToDo_Item = {
      id: 1 + Math.random(),
      value: this.state.new_ToDo_Item.slice()
    }

    const list = [...this.state.list]

    list.push(new_ToDo_Item);

    this.setState({
      list,
      new_ToDo_Item: ""
    })

  }

  delete_item(id) {

    const list = [...this.state.list]

    const newlist = list.filter(item => item.id !== id)

    this.setState({
      list: newlist,
      new_ToDo_Item: ""
    })

  }





  render() {
    return (
      <div>
        <h1 className="app-title">ToDo List</h1>
        <div className="Container" >


          <div
            style={{
              padding: 30,
              textAlign: "left",
              maxWidth: 500,
              margin: "auto"
            }}
          ></div>

          <br />
          <input
            placeholder="what do you want toDo ðŸ˜Š ?"

            type="text"

            id="item2"

            value={this.state.new_ToDo_Item}

            onChange={e => this.update_new_to_do_item("new_ToDo_Item", e.target.value)}
          >
          </input>
          <button onClick={() => this.add_new_item()} > + </button>
          <br />
          <ul>
            {this.state.list.map(item => {
              return (
                <li key={item.id} >

                  {this.state.new_ToDo_Item}
                  <button className="btn btn-floating" onClick={() => this.delete_item(item.id)} >
                    x
                </button>

                </li>
              )


            })}
          </ul>
        </div>
      </div>
    );
  }


}
export default App;
