    this.setState({
      [key]: value
    })

  }


  add_new_item() {

    const new_ToDo_Item = {
      id: 1 + Math.random(),
      value: this.state.new_ToDo_Item
    }

    const list = [...this.state.list, new_ToDo_Item]

    this.setState({
      list,
      new_ToDo_Item: ""
    })

  }

  delete_item(id) {

    const list = [...this.state.list]

    const newlist = list.filter(item => item.id !== id)

    this.setState({
      list: newlist,
      new_ToDo_Item: ""
    })

  }





  render() {
    console.log(this.state);
    return (
      <div>
        <h1 className="app-title">ToDo List</h1>
        <div className="Container" id="center" >


          <div
            style={{

            }}
          ></div>
          add an item
          <br />
          <input
            placeholder="what do you want toDo ðŸ˜Š ?"

            type="text"

            id="item2"

            value={this.state.new_ToDo_Item}

            onChange={e => this.update_new_to_do_item("new_ToDo_Item", e.target.value)}
          >
          </input>
          <button
            className="add-btn btn-floating"
            onClick={() => this.add_new_item()}
            disabled={!this.state.new_ToDo_Item.length}
            >

            <i
            className="material-icons"
            >+</i>
            </button>
          <br /><br/>
          <ul>
            {this.state.list.map((item, index) => {
              console.log(item);
              return (
                <li key={index} >
                  {item.value}
                  <button className="btn btn-floating" onClick={() => this.delete_item(item.id)} >
                  <i className="material-icons">x</i>
                </button>
                </li>
              )


            })}
          </ul>
        </div>
      </div>
    );
  }


}
